pragma solidity 0.8.23;

contract Endian {
  /// reverses byte order to allow conversion between little endian and big
  /// endian integers
  function _byteSwap(bytes32 input) internal pure returns (bytes32 output) {
    output = output | bytes1(input);
    for (uint i = 1; i < 32; i++) {
      output = output >> 8;
      output = output | bytes1(input << (i * 8));
    }
  }
}
","keccak256":"0xd6acc2a19aaedc1b903681fedf6e302aabdd33695dee215e21b5190a83d0769e","license":"MIT"},"contracts/Groth16.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.23;

struct G1Point {
  uint256 x;
  uint256 y;
}

// A field element F_{p^2} encoded as `real + i * imag`.
// We chose to not represent this as an array of 2 numbers, because both Circom
// and Ethereum EIP-197 encode to an array, but with conflicting encodings.
struct Fp2Element {
  uint256 real;
  uint256 imag;
}

struct G2Point {
  Fp2Element x;
  Fp2Element y;
}

struct Groth16Proof {
  G1Point a;
  G2Point b;
  G1Point c;
}

interface IGroth16Verifier {
  function verify(
    Groth16Proof calldata proof,
    uint256[] calldata pubSignals
  ) external view returns (bool);
}
